pragma solidity ^0.5.11;
pragma experimental ABIEncoderV2;

contract EternalStorage {
  /*
validationType
1: AllSignatures
2: MultipleSignatures
3: Petition
*/
  struct ContractDetails {
    uint256 validationType;
    address contractAddress;
  }

  mapping(address => ContractDetails[]) public uploaderContractsMap;
  mapping(address => ContractDetails[]) public signatoryContractsMap;
  mapping(bytes32 => ContractDetails[]) public hashMap;

  mapping(bytes32 => uint256) public metrics;

  mapping(address => bool) public isAllowedFactoryContracts;
  mapping(address => bool) public isAllowedSignatureContracts;

  mapping(address => bool) public isAdmins;

  constructor() public {
    isAdmins[msg.sender] = true;
  }

  modifier isAdmin() {
    require(isAdmins[msg.sender], "Account is not admin");
    _;
  }

  modifier isAllowedFactory() {
    require(isAllowedFactoryContracts[msg.sender], "Factory does not have permissions");
    _;
  }

  //admin functions

  /// @notice Adds factory contract to allowed list
  /// @param _newVersion Address of factory contract to be added.
  function addFactoryContract(address _newVersion) public isAdmin() {
    require(!isAllowedFactoryContracts[_newVersion], "Factory address already exists");
    isAllowedFactoryContracts[_newVersion] = true;
  }

  /// @notice Removes factory contract from allowed list
  /// @param _oldVersion Address of factory contract to be removed.
  function deleteFactoryContract(address _oldVersion) public isAdmin() {
    require(isAllowedFactoryContracts[_oldVersion], "Factory address does not exist");
    isAllowedFactoryContracts[_oldVersion] = false;
  }

  /// @notice Give admin rights to an address
  /// @param _address Address of account to be given rights to.
  function setAdmin(address _address) external isAdmin() {
    isAdmins[_address] = true;
  }

  /// @notice Remove admin rights to an address
  /// @param _address Address of admin to be removed.
  function deleteAdmin(address _address) external isAdmin() {
    isAdmins[_address] = false;
  }

  /// @notice Array of addresses which account has created
  /// @param _address Address of account
  /// @return array of struct containing contract address and type of contract. 1: AllSig, 2: MultiSig, 3: Petition
  function getUploaderContracts(address _address)
    external
    view
    returns (ContractDetails[] memory)
  {
    return uploaderContractsMap[_address];
  }

  /// @notice Array of addresses where the account has been requested to sign on a document
  /// @param _address Address of account to be queried.
  /// @return array of struct containing contract address and type of contract. 1: AllSig, 2: MultiSig, 3: Petition
  function getSignatoryContracts(address _address)
    external
    view
    returns (ContractDetails[] memory)
  {
    return signatoryContractsMap[_address];
  }

  /// @notice Appends the signature contract to a list of contracts where account uploaded document.
  /// @param _validationType Type of signature contract 1: AllSig, 2: MultiSig, 3: Petition
  /// @param _uploaderAddress Address of document uploader.
  /// @param _contractAddress address of contract to be appended to list.
  /// @param _hash Document hash so document hash can be mapped to a list of contract addresses.
  function addUploaderContract(
    uint256 _validationType,
    address _uploaderAddress,
    address _contractAddress,
    bytes32 _hash
  ) external isAllowedFactory returns (bool) {
    uploaderContractsMap[_uploaderAddress].push(
      ContractDetails(_validationType, _contractAddress)
    );
    hashMap[_hash].push(ContractDetails(_validationType, _contractAddress));
    isAllowedSignatureContracts[_contractAddress] = true;
  }

  /// @notice Appends the signature contract to a list of contracts where account is required to sign.
  /// @param _validationType Type of signature contract 1: AllSig, 2: MultiSig
  /// ****** Petition contract not added to list as address cannot be explicitly requested for signing.
  /// @param _signatoryAddress Address of account to add contract to.
  /// @param _contractAddress address of contract to be appended to list.
  function addSignatoryContract(
    uint256 _validationType,
    address _signatoryAddress,
    address _contractAddress
  ) external authorizedContract returns (bool) {
    signatoryContractsMap[_signatoryAddress].push(
      ContractDetails(_validationType, _contractAddress)
    );
  }

  modifier authorizedContract() {
    require(
      isAllowedSignatureContracts[msg.sender] || isAllowedFactoryContracts[msg.sender],
      "Contract does not have permissions to update eternal storage"
    );
    _;
  }

  /// @notice Get all signature contracts with the given document hash
  /// @param _hash document hash that is uploaded for signing
  /// @return array of struct containing address of contract and document hash of contract
  function getAddressFromHash(bytes32 _hash)
    external
    view
    returns (ContractDetails[] memory)
  {
    return hashMap[_hash];
  }

  /// @notice increments the value assigned to the metric key by one.
  /// @param _key key of metric e.g ISSUED_ALLSIG
  function incrementMetrics(bytes32 _key) external authorizedContract() {
    metrics[_key]++;
  }

  function incrementMetrics(bytes32 _key, uint value) external authorizedContract() {
    metrics[_key] += value;
  }

  /// @notice Number of times account has uploaded a document for signing
  /// @param _address Address of account
  /// @return Number of times account has uploaded a document for signing
  function getUploaderCount(address _address) external view returns (uint256) {
    return uploaderContractsMap[_address].length;
  }

  /// @notice Number of times the account has been requested to sign on a document
  /// @param _address Address of account
  /// @return Number of times the account has been requested to sign on a document
  function getSigningInvitationCount(address _address) external view returns (uint256) {
    return signatoryContractsMap[_address].length;
  }

  /// @notice Number of times the account has been requested to sign on a document
  /// @param _key key of metric e.g ISSUED_ALLSIG
  /// @return metric value of given key
  function getMetricsValue(bytes32 _key) external view returns (uint256) {
    return metrics[_key];
  }

  /// @notice Checks whether provided account is admin
  /// @dev called by factories when checking to see if msg.sender of eternalStorage address change is admin
  /// @param _address address of account to check if admin
  function isAccountAdmin(address _address) external view returns (bool) {
    return isAdmins[_address];
  }
}

